import React from 'react'
import "./Join.css"
import logo from "../../images/logo.png"
import { Link } from 'react-router-dom'

let user;

const Join = () => {
  
  const sendUser = () =>{
    user = document.getElementById('joinInput').value;
  }

  const handleKeyPress=(e)=>{
    if(e.key==="Enter" && document.getElementById('joinInput').value){
      if(document.getElementById('joinInput').value)
      document.querySelector('.joinbtn').click();
    }
  }

  return (
    <div>
        <div className='JoinPage'>
            <div className='JoinContainer'>
              <img src = {logo} alt="logo"/>
              <h1>Chatter</h1>
              <input placeholder='Enter Your Name'
                type='text' 
                id='joinInput'
                onKeyDown={handleKeyPress}></input>
              <Link to="/chat"><button onClick={sendUser} className='joinbtn'>Login</button></Link>
            </div>
        </div>
    </div>
  )
}

export default Join;
export {user};
