import React from 'react'
import "./Join.css"
import logo from "../../images/logo.png"
import { Link } from 'react-router-dom'

let user;

const Join = () => {
  
  const sendUser = () =>{
    user = document.getElementById('joinInput').ariaValueMax;
  }
    const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      sendUser();
    }
  }

  return (
    <div>
        <div className='JoinPage'>
            <div className='JoinContainer'>
              <img src = {logo} alt="logo"/>
              <h1>Chatter</h1>
              <input placeholder='Enter Your Name' type='text' id='joinInput' onKeyDown={handleKeyDown}></input>
              <Link to="/chat"><button onClick={sendUser} onKeyDown={(e)=>{
                if(e.key==="Enter") sendUser();
              }} className='joinbtn'>Login</button></Link>
            </div>
        </div>
    </div>
  )
}

export default Join;
export {user};
